import React, { useState, useEffect } from 'react';

const TaskForm = ({ initialTask = {}, onSubmit }) => {
  const [title, setTitle] = useState(initialTask.title || '');
  const [description, setDescription] = useState(initialTask.description || '');
  const [dueDate, setDueDate] = useState(initialTask.dueDate || '');
  const [errors, setErrors] = useState({});

  useEffect(() => {
    setTitle(initialTask.title || '');
    setDescription(initialTask.description || '');
    setDueDate(initialTask.dueDate || '');
  }, [initialTask]);

  const validate = () => {
    const errors = {};
    if (!title.trim()) errors.title = 'El título es obligatorio';
    if (dueDate && isNaN(Date.parse(dueDate))) errors.dueDate = 'Fecha inválida';
    setErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validate()) return;
    onSubmit({ title, description, dueDate });
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Título * </label>
        <input
          type="text"
          value={title}
          onChange={e => setTitle(e.target.value)}
        />
        {errors.title && <p className="error">{errors.title}</p>}
      </div>

      <div>
        <label>Descripción</label>
        <textarea
          value={description}
          onChange={e => setDescription(e.target.value)}
        />
      </div>

      <div>
        <label>Fecha de entrega </label>
        <input
          type="date"
          value={dueDate}
          onChange={e => setDueDate(e.target.value)}
        />
        {errors.dueDate && <p className="error">{errors.dueDate}</p>}
      </div>

      <button type="submit">Guardar</button>
    </form>
  );
};

export default TaskForm;
