import React, { useState } from 'react';

const TaskForm = ({ initialTask = {}, onSubmit }) => {
  // Se inicializa una sola vez sin usar useEffect para evitar reseteo al escribir
  const [title, setTitle] = useState(initialTask.title || '');
  const [description, setDescription] = useState(initialTask.description || '');
  const [dueDate, setDueDate] = useState(initialTask.dueDate || '');
  const [errors, setErrors] = useState({});

  const validate = () => {
    const newErrors = {};

    if (!title.trim()) {
      newErrors.title = 'El título es obligatorio';
    }

    if (dueDate && isNaN(Date.parse(dueDate))) {
      newErrors.dueDate = 'La fecha no es válida';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!validate()) return;

    onSubmit({
      title: title.trim(),
      description: description.trim(),
      dueDate: dueDate || null,
    });

    // Opcional: limpiar el formulario si estás en modo "crear"
    if (!initialTask.id) {
      setTitle('');
      setDescription('');
      setDueDate('');
      setErrors({});
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Título *</label>
        <input
          type="text"
          value={title}
          onChange={e => setTitle(e.target.value)}
          placeholder="Escribe un título"
        />
        {errors.title && <p className="error">{errors.title}</p>}
      </div>

      <div>
        <label>Descripción</label>
        <br />
        <textarea
          value={description}
          onChange={e => setDescription(e.target.value)}
          placeholder="Descripción (opcional)"
        />
      </div>

      <div>
        <label>Fecha de entrega </label><br />
        <input
          type="date"
          value={dueDate}
          onChange={e => setDueDate(e.target.value)}
        />
        {errors.dueDate && <p className="error">{errors.dueDate}</p>}
      </div>

      <button type="submit">Guardar</button>
    </form>
  );
};

export default TaskForm;
